"""# <font color='red'> 8) Create a class called Circle which calculates the area and circumference of a circle  """

class Circle:
  def __init__(self, radius):
    self.radius = radius
  def calculate_area(self):
     return self.radius**2*3.14

  def calculate_circum(self):
     return self.radius*2*3.14
  def __str__(self):
    return 'Circumference equals {} and area equals {} '.format(self.calculate_circum(),self.calculate_area())

circle1 = Circle(9)
